CREATE TABLE `test2`.`user` ( `id` INT(6) NOT NULL AUTO_INCREMENT , `t_id` BIGINT(12) NOT NULL , `token` VARCHAR(255) NOT NULL , `token_secret` VARCHAR(255) NOT NULL , `screen_name` VARCHAR(255) NOT NULL , `created_at` TIMESTAMP NOT NULL , PRIMARY KEY (`id`), INDEX `twitte_id` (`t_id`)) ENGINE = InnoDB;

CREATE TABLE `test2`.`tweets` ( `id` BIGINT(19) NOT NULL AUTO_INCREMENT , `tw_id` BIGINT(19) NOT NULL , `text` VARCHAR(255) NOT NULL , `id_user` BIGINT(19) NOT NULL , `timestamp` VARCHAR(255) NOT NULL , `retweeted` BIGINT(19) NOT NULL , `created_at` TIMESTAMP NOT NULL , PRIMARY KEY (`id`)) ENGINE = InnoDB;

CREATE TABLE `test2`.`tweet_user` ( `id` BIGINT(19) NOT NULL AUTO_INCREMENT , `twu_id` BIGINT(19) NOT NULL , `name` VARCHAR(255) NOT NULL , `screen_name` VARCHAR(255) NOT NULL , `url` VARCHAR(255) NOT NULL , `desciption` VARCHAR(255) NOT NULL , `followers_count` INT(10) NOT NULL , `friends_count` INT(10) NOT NULL , `status_count` INT(10) NOT NULL , `created_at` TIMESTAMP NOT NULL , PRIMARY KEY (`id`)) ENGINE = InnoDB;

CREATE TABLE `test2`.`rtweeted_user` ( `id` BIGINT(19) NOT NULL AUTO_INCREMENT , `rtu_id` BIGINT(19) NOT NULL , `name` VARCHAR(255) NOT NULL , `screen_name` VARCHAR(255) NOT NULL , `url` VARCHAR(255) NOT NULL , `description` VARCHAR(255) NOT NULL , `followers_count` INT(10) NOT NULL , `friends_count` INT(10) NOT NULL , `status_count` INT(10) NOT NULL , `created_at` TIMESTAMP NOT NULL , PRIMARY KEY (`id`)) ENGINE = InnoDB;